var JSONPairs = [];
const fs = require('fs');

async function preloadStuff() {
    await fs.readFile('resources/deathBattleTemp/actionPossibilities.json', function read(err, data) {
        if (err) {
            throw err;
        }

        const outputData = JSON.parse(data);

        for(var i in outputData){
            var val = outputData[i];
            for(var j in val){
                var sub_key = j;
                JSONPairs.push(sub_key.toLowerCase());
            }
        }
    });
    setupPrompt();
}

function setupPrompt() {
    msg.delete();
